// Simple version: Return abstract for RAG with helpful error messages
const query = $('Webhook').item.json.body.query || '';

// Parse response
let responseData = items[0].json;
let searchData;

try {
  searchData = typeof responseData.data === 'string' 
    ? JSON.parse(responseData.data) 
    : responseData.data || responseData;
} catch (e) {
  searchData = {};
}

const results = [];

// Always use AbstractText if available (best for RAG)
if (searchData.AbstractText) {
  results.push({
    title: searchData.Heading || 'Wikipedia',
    snippet: searchData.AbstractText,
    url: searchData.AbstractURL || '',
    source: 'Wikipedia',
    rank: 1
  });

  // Optional: Add capital from Infobox if query asks about capital
  if (query.toLowerCase().includes('capital') && searchData.Infobox && searchData.Infobox.content) {
    const capitalInfo = searchData.Infobox.content.find(item => item.label === 'Capital');
    if (capitalInfo) {
      results[0].snippet += `\n\nThe capital of ${searchData.Heading || 'this country'} is ${capitalInfo.value}.`;
    }
  }
} else {
  // Fallback to other content
  if (searchData.Results && Array.isArray(searchData.Results) && searchData.Results.length > 0) {
    searchData.Results.slice(0, 2).forEach((result, idx) => {
      if (result.Text && result.FirstURL) {
        const cleanText = result.Text.replace(/<[^>]*>/g, '');
        results.push({
          title: cleanText,
          snippet: cleanText,
          url: result.FirstURL,
          source: 'DuckDuckGo',
          rank: idx + 1
        });
      }
    });
  }
}

// Add helpful message when no results found
let helpMessage = null;
if (results.length === 0) {
  helpMessage = 'The web search API (DuckDuckGo Instant Answer) could not find results for this query. ' +
                'This API works best for well-known topics with Wikipedia entries. ' +
                'Try rephrasing your question to be more specific (e.g., "Python programming language" instead of "what is python?").';
}

return [{
  json: {
    query: query,
    source: 'duckduckgo',
    results: results,
    count: results.length,
    success: results.length > 0,
    help_message: helpMessage
  }
}];
