// Parse Serper.dev response for RAG
const query = $('Webhook').item.json.body.query || '';
const maxResults = $('Webhook').item.json.body.max_results || 5;

// Parse response
let responseData = items[0].json;
let searchData;

try {
  searchData = typeof responseData === 'string' 
    ? JSON.parse(responseData) 
    : responseData;
} catch (e) {
  searchData = {};
}

const results = [];

// Priority 1: Answer Box (direct answer from Google)
if (searchData.answerBox) {
  const box = searchData.answerBox;
  results.push({
    title: box.title || 'Answer',
    snippet: box.answer || box.snippet || '',
    url: box.link || '',
    source: 'Google Answer Box',
    rank: 1,
    type: 'answer'
  });
}

// Priority 2: Knowledge Graph (for entities)
if (searchData.knowledgeGraph && searchData.knowledgeGraph.description) {
  results.push({
    title: searchData.knowledgeGraph.title || 'Knowledge Graph',
    snippet: searchData.knowledgeGraph.description,
    url: searchData.knowledgeGraph.descriptionLink || searchData.knowledgeGraph.website || '',
    source: 'Google Knowledge Graph',
    rank: results.length + 1,
    type: 'knowledge'
  });
}

// Priority 3: Organic search results
if (searchData.organic && Array.isArray(searchData.organic)) {
  searchData.organic.slice(0, maxResults).forEach((result, idx) => {
    if (result.snippet && result.link) {
      results.push({
        title: result.title || 'Search Result',
        snippet: result.snippet,
        url: result.link,
        source: 'Google Search',
        rank: results.length + 1,
        type: 'organic',
        position: result.position || idx + 1
      });
    }
  });
}

// Add helpful message when no results found
let helpMessage = null;
if (results.length === 0) {
  helpMessage = 'The web search API (Serper.dev) could not find results for this query. ' +
                'Please try rephrasing your question or check if the API key is valid.';
}

return [{
  json: {
    query: query,
    source: 'serper.dev',
    provider: 'Google (via Serper.dev)',
    results: results,
    count: results.length,
    success: results.length > 0,
    help_message: helpMessage,
    raw_response: searchData  // For debugging
  }
}];
